package com.example.appointment_module.controllers;import com.example.appointment_module.domain.Appointment;import com.example.appointment_module.services.AppointmentService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;import java.util.Optional;import java.util.Set;@RestController@RequestMapping("/appointments")public class AppointmentController {    @Autowired    private AppointmentService appointmentService;    @GetMapping    public List<Appointment> getAllAppointments() {        return appointmentService.findAll();    }    @GetMapping("/{id}")    public Optional<Appointment> getAppointmentById(@PathVariable Long id) {        return appointmentService.findById(id);    }    @GetMapping("/patient/{id}")    public Set<Appointment> getAppointmentsByPatientId(@PathVariable Long id) {        return appointmentService.findByPatientId(id);    }    @GetMapping("/doctor/{id}")    public Set<Appointment> getAppointmentsByDoctorId(@PathVariable Long id) {        return appointmentService.findByDoctorId(id);    }    @PostMapping    public Appointment createAppointment(@RequestBody Appointment appointment) {        return appointmentService.save(appointment);    }    @PutMapping("/{id}")    public Appointment updateAppointment(@PathVariable Long id, @RequestBody Appointment appointment) {        appointment.setId(id);        return appointmentService.update(appointment);    }    @DeleteMapping("/{id}")    public void deleteAppointment(@PathVariable Long id) {        appointmentService.delete(id);    }}