package com.example.appointment_module.services;import com.example.appointment_module.domain.Appointment;import com.example.appointment_module.repositories.AppointmentRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class AppointmentServiceImpl implements AppointmentService {    @Autowired    private AppointmentRepository appointmentRepository;    @Override    public List<Appointment> findAll() {        return (List<Appointment>) appointmentRepository.findAll();    }    @Override    public Optional<Appointment> findById(Long id) {        return appointmentRepository.findById(id);    }    @Override    public Appointment save(Appointment appointment) {        return appointmentRepository.save(appointment);    }    @Override    public Appointment update(Appointment appointment) {        return appointmentRepository.save(appointment);    }    @Override    public void delete(Long id) {        appointmentRepository.deleteById(id);    }}