package sdtlab.patient_module.bootstrap;import sdtlab.patient_module.domain.*;import sdtlab.patient_module.repositories.*;import org.springframework.boot.CommandLineRunner;import org.springframework.stereotype.Component;import java.sql.Date;@Componentpublic class DataLoader implements CommandLineRunner {    private final PatientRepository patientRepository;    private final DoctorRepository doctorRepository;    public DataLoader(PatientRepository patientRepository, DoctorRepository doctorRepository) {        this.patientRepository = patientRepository;        this.doctorRepository = doctorRepository;    }    @Override    public void run(String... args) throws Exception {        if (patientRepository.count() > 0) {            System.out.println("Patient data already loaded");            return;        }        System.out.println("Loading patient data...");        Patient patient1 = new Patient(                "John", "Doe",                "john.doe@example.com",                "+1234567890",                "123 Main St, City",                "M",                Date.valueOf("1985-05-15")         );        patientRepository.save(patient1);        Doctor doctor1 = new Doctor(                "Dr. Jane",                "Smith",                "jane.smith@example.com",                "+9876543210",                "123 Hospital Rd",                "F",                Date.valueOf("1975-03-22"),                Specializations.CARDIOLOGIST        );        doctorRepository.save(doctor1);        System.out.println("Patient data loaded successfully.");    }}