package sdtlab.patient_module.controllers;import sdtlab.patient_module.domain.Patient;import sdtlab.patient_module.services.PatientService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/patients")public class PatientController {    private final PatientService patientService;    @Autowired    public PatientController(PatientService patientService) {        this.patientService = patientService;    }    @GetMapping("/")    public ResponseEntity<List<Patient>> getAllPatients() {        return ResponseEntity.ok(patientService.findAll());    }    @GetMapping("/{id}")    public ResponseEntity<Patient> getPatientById(@PathVariable Long id) {        return patientService.findById(id)                .map(ResponseEntity::ok)                .orElseGet(() -> ResponseEntity.notFound().build());    }    @PostMapping("/")    public ResponseEntity<Patient> createPatient(@RequestBody Patient patient) {        return ResponseEntity.ok(patientService.save(patient));    }    @PutMapping("/{id}")    public ResponseEntity<Patient> updatePatient(@PathVariable Long id, @RequestBody Patient patient) {        patient.setId(id);        return ResponseEntity.ok(patientService.update(patient));    }    @DeleteMapping("/{id}")    public ResponseEntity<Void> deletePatient(@PathVariable Long id) {        patientService.delete(id);        return ResponseEntity.noContent().build();    }}