package sdtlab.patient_module.services;import sdtlab.patient_module.domain.Doctor;import sdtlab.patient_module.repositories.DoctorRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class DoctorServiceImpl implements DoctorService {    private final DoctorRepository doctorRepository;    @Autowired    public DoctorServiceImpl(DoctorRepository doctorRepository) {        this.doctorRepository = doctorRepository;    }    @Override    public List<Doctor> findAll() {        return (List<Doctor>) doctorRepository.findAll();    }    @Override    public Optional<Doctor> findById(Long id) {        return doctorRepository.findById(id);    }    @Override    public Doctor save(Doctor doctor) {        return doctorRepository.save(doctor);    }    @Override    public Doctor update(Doctor doctor) {        return doctorRepository.save(doctor);    }    @Override    public void delete(Long id) {        doctorRepository.deleteById(id);    }}