package sdtlab.patient_module.services;import sdtlab.patient_module.domain.Patient;import sdtlab.patient_module.repositories.PatientRepository;import sdtlab.patient_module.services.PatientService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class PatientServiceImpl implements PatientService {    private final PatientRepository patientRepository;    @Autowired    public PatientServiceImpl(PatientRepository patientRepository) {        this.patientRepository = patientRepository;    }    @Override    public List<Patient> findAll() {        return (List<Patient>) patientRepository.findAll();    }    @Override    public Optional<Patient> findById(Long id) {        return patientRepository.findById(id);    }    @Override    public Patient save(Patient patient) {        return patientRepository.save(patient);    }    @Override    public Patient update(Patient patient) {        return patientRepository.save(patient);    }    @Override    public void delete(Long id) {        patientRepository.deleteById(id);    }}