package com.example.prescription_module.controllers;import com.example.prescription_module.domain.Medication;import com.example.prescription_module.services.MedicationService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/medications")public class MedicationController {    private final MedicationService medicationService;    @Autowired    public MedicationController(MedicationService medicationService) {        this.medicationService = medicationService;    }    @GetMapping("/")    public ResponseEntity<List<Medication>> getAllMedications() {        return ResponseEntity.ok(medicationService.findAll());    }    @GetMapping("/{id}")    public ResponseEntity<Medication> getMedicationById(@PathVariable Long id) {        return medicationService.findById(id)                .map(ResponseEntity::ok)                .orElseGet(() -> ResponseEntity.notFound().build());    }    @PostMapping("/")    public ResponseEntity<Medication> createMedication(@RequestBody Medication medication) {        return ResponseEntity.ok(medicationService.save(medication));    }    @PutMapping("/{id}")    public ResponseEntity<Medication> updateMedication(@PathVariable Long id, @RequestBody Medication medication) {        medication.setId(id);        return ResponseEntity.ok(medicationService.update(medication));    }    @DeleteMapping("/{id}")    public ResponseEntity<Void> deleteMedication(@PathVariable Long id) {        medicationService.delete(id);        return ResponseEntity.noContent().build();    }}