package com.example.prescription_module.domain;import jakarta.persistence.*;import java.util.HashSet;import java.util.Set;import lombok.Getter;import lombok.Setter;@Entitypublic class Medication {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String name;    public Medication() {    }    public Medication(String name) {        this.name = name;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }}