package com.example.prescription_module.domain;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;@Entity@Getter@Setterpublic class Medication_Prescription {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private Long idPrescription;    private Long idMedication;    private String dosage;    private String instructions;    public Medication_Prescription() {    }    public Medication_Prescription(Long idPrescription, Long idMedication, String dosage, String instructions) {        this.idPrescription = idPrescription;        this.idMedication = idMedication;        this.dosage = dosage;        this.instructions = instructions;    }}