package com.example.prescription_module.services;import com.example.prescription_module.domain.Medication;import com.example.prescription_module.repositories.MedicationRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class MedicationServiceImpl implements MedicationService {    @Autowired    private MedicationRepository medicationRepository;    @Override    public List<Medication> findAll() {        return (List<Medication>) medicationRepository.findAll();    }    @Override    public Optional<Medication> findById(Long id) {        return medicationRepository.findById(id);    }    @Override    public Medication save(Medication medication) {        return medicationRepository.save(medication);    }    @Override    public Medication update(Medication medication) {        return medicationRepository.save(medication);    }    @Override    public void delete(Long id) {        medicationRepository.deleteById(id);    }}